mapper.insert:
		INSERT INTO GOODS(GOODS_NO
                , CATEGORY
                , GOODS_NAME
                , GOODS_THUMBNAIL
                , GOODS_CONTENT
                , GOODS_INFO
                , MARK
                , PRICE
                , TOTAL_STOCK
                , REMAIN_STOCK)
           VALUES(GOODS_SEQ.NEXTVAL
                , #{category}
                , #{goodsName}
                , #{goodsThumbnail}
                , #{goodsContent}
                , #{goodsInfo}
                , #{mark}
                , #{price}
                , #{totalStock}
                , #{totalStock})

mapper.select:
		SELECT GOODS_NO, GOODS_NAME, PRICE, DISCOUNT, TOTAL_STOCK, REMAIN_STOCK, STATUS
 		  FROM GOODS
		 WHERE GOODS_NO = #{goodsNo}

mapper.update:
 		UPDATE GOODS
		   SET PRICE = #{price}
		     , DISCOUNT = #{discount}
		     , TOTAL_STOCK = #{totalStock}
		     , REMAIN_STOCK = #{remainStock}
		     , STATUS = #{status}
		 WHERE GOODS_NO = #{goodsNo}

		UPDATE GOODS
		   SET CATEGORY = #{category}
            , GOODS_NAME = #{goodsName}
            , GOODS_THUMBNAIL = #{goodsThumbnail}
            , GOODS_CONTENT = #{goodsContent}
            , GOODS_INFO = #{goodsInfo}
            , MARK = #{mark}
            , PRICE = #{price}
            , TOTAL_STOCK = #{totalStock}
            , REMAIN_STOCK = #{totalStock}
            , STATUS = #{status}
		 WHERE GOODS_NO = #{goodsNo}

mapper.select:
 		SELECT COUNT(*)
		  FROM DIBS
		 WHERE DIBS_MEMBER = #{memberNo}
		   AND DIBS_GOODS = #{goodsNo}

mapper.delete:
 		DELETE
 		  FROM DIBS
 		 WHERE DIBS_MEMBER = #{memberNo}
		   AND DIBS_GOODS = #{goodsNo}

mapper.select:
 		SELECT COUNT(*)
		  FROM DIBS
		 WHERE DIBS_MEMBER = #{memberNo}
