mapper.insert:
		INSERT INTO MEMBER(MEMBER_NO
                , MEMBER_ID
                , MEMBER_PWD
                , MEMBER_NAME
                , BIRTH_DATE
                , PHONE
                , EMAIL
                , ADDRESS)
           VALUES(MEMBER_SEQ.NEXTVAL
                , #{memberId}
                , #{memberPwd}
                , #{memberName}
                , #{birthDate}
                , #{phone}
                , #{email}
                , #{address})

mapper.select:
 		SELECT COUNT(*)
		  FROM MEMBER
		 WHERE MEMBER_ID = #{checkId}

SELECT COUNT(*)
  FROM MEMBER
 WHERE EMAIL = #{checkEmail}

mapper.insert:
 		INSERT INTO IDENTIFICATION(EMAIL
		                         , AUTH_CODE
		                         , AUTH_CODE_DATE)
		                    VALUES(#{email}
		                         , #{authCode}
		                         , SYSDATE)

mapper.select:
 		SELECT AUTH_CODE
		  FROM IDENTIFICATION
		 WHERE EMAIL = #{email}
		   AND AUTH_CODE = #{authCode}
		   AND AUTH_CODE_DATE > SYSDATE - (3 / 1440)

 		SELECT MEMBER_ID
		  FROM MEMBER
		 WHERE MEMBER_NAME = #{memberName}
		   AND EMAIL = #{email}
		   AND STATUS = 'Y'

 		SELECT *
		  FROM MEMBER
		 WHERE MEMBER_NAME = #{memberName}
		   AND MEMBER_ID = #{memberId}
		   AND EMAIL = #{email}
		   AND STATUS = 'Y'

mapper.update:
 		UPDATE MEMBER
		   SET MEMBER_PWD = #{memberPwd}
		 WHERE MEMBER_NAME = #{memberName}
		   AND MEMBER_ID = #{memberId}
		   AND EMAIL = #{email}
		   AND STATUS = 'Y'

UPDATE MEMBER
   SET BIRTH_DATE = #{birthDate}
     , PHONE = #{phone}
     , ADDRESS = #{address}
 WHERE MEMBER_NO = #{memberNo}
   AND STATUS = 'Y'
